from traducao import tableCodons, getComplement


def traduction(sequence: str, tableCodons: dict) -> str:
    translated = ""
   
    for i in range(0, len(sequence), 3):
        
        codon = sequence[i:i+3]
        # parada : (TAA, TAG, TGA)
        #print(codon)

        if len(codon) == 3:  
            amino = tableCodons.get(codon, "")
            if amino != "Stop":
                translated += amino + "-" 
                #print(translated) 
                
            else:
                translated += "* (parada)-"
                 
        #print(translated)
    return translated.rstrip("-") 

